import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';
import { nodeInputStyle, labelStyle, onFieldFocus, onFieldBlur } from "../styles";

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || 'type {{input}} here');
  const [variables, setVariables] = useState([]);
  const textAreaRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);

    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${Math.max(textAreaRef.current.scrollHeight, 60)}px`;
    }
  }, [currText, id, updateNodeField]);

  const handles = [
    { type: 'source', position: Position.Right, id: 'output' },
    ...variables.map((v, i) => ({
      type: 'target', position: Position.Left, id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))
  ];

  // Shared Styles to ensure both layers overlap exactly
  const sharedStyles = {
    ...nodeInputStyle,
    fontSize: '13px',
    lineHeight: '28px', 
    padding: '12px',
    whiteSpace: 'pre-wrap',
    wordWrap: 'break-word',
    fontFamily: 'monospace', // Monospace is required for exact alignment
    boxSizing: 'border-box',
    margin: 0,
    border: '1.5px solid transparent',
    textAlign: 'left',
  };

  const renderVisualContent = () => {
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);
    return parts.map((part, i) => {
      const match = part.match(/\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/);
      if (match) {
        return (
          <span key={i} style={{
            display: 'inline-flex',
            alignItems: 'center',
            backgroundColor: '#eef2ff',
            border: '1px solid #c7d2fe',
            borderRadius: '4px',
            padding: '0 6px',
            margin: '0 1px',
            color: '#4338ca',
            fontSize: '12px',
            fontWeight: '600',
            verticalAlign: 'middle',
            height: '22px',
          }}>
            <span style={{ marginRight: '4px' }}>➡️</span>
            {match[1]}
            <span style={{ marginLeft: '6px', color: '#94a3b8' }}>×</span>
          </span>
        );
      }
      return <span key={i} style={{ color: '#334155' }}>{part}</span>;
    });
  };

  return (
    <BaseNode id={id} label="Text" handles={handles}>
      <label style={labelStyle}>CONTENT</label>
      <div style={{ position: 'relative', width: '100%', minHeight: '60px' }}>

        {/* BOTTOM LAYER: Visual Rendering (Text + Pills) */}
        <div style={{ 
          ...sharedStyles,
          position: 'absolute', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          zIndex: 0,
          pointerEvents: 'none', 
          background: '#f8fafc', // Light gray background
          borderRadius: '8px',
          border: '1.5px solid #e2e8f0',
        }}>
          {renderVisualContent()}
        </div>

        {/* TOP LAYER: Interaction (Transparent Textarea) */}
        <textarea 
          ref={textAreaRef}
          value={currText} 
          onChange={(e) => setCurrText(e.target.value)}
          onFocus={onFieldFocus}
          onBlur={onFieldBlur}
          spellCheck={false}
          style={{ 
            ...sharedStyles,
            position: 'relative',
            zIndex: 1,
            background: 'transparent', // Let bottom layer show through
            color: 'transparent',      // Hide the raw typing so it doesn't overlap
            caretColor: '#6366f1',      // Keep the cursor visible
            resize: 'none',
            overflow: 'hidden',
            width: '100%',
            outline: 'none',
          }} 
        />
      </div>
    </BaseNode>
  );
};
