import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';

export const TextNode = ({ id, data }) => {
  const [variables, setVariables] = useState([]);
  const editableRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  // Function to extract text and update variables/store
  const handleInput = () => {
    const text = editableRef.current.innerText;

    // Detect variables for handles
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...text.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);

    updateNodeField(id, 'text', text);
  };

  const handles = [
    { type: 'source', position: Position.Right, id: 'output' },
    ...variables.map((v, i) => ({
      type: 'target', position: Position.Left, id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))
  ];

  return (
    <BaseNode id={id} label="Text" handles={handles}>
      <label style={{ display: 'block', color: '#64748b', fontSize: '10px', fontWeight: 'bold', marginBottom: '8px' }}>
        CONTENT
      </label>

      <div 
        ref={editableRef}
        contentEditable
        onInput={handleInput}
        suppressContentEditableWarning={true}
        style={{
          minHeight: '80px',
          width: '240px',
          backgroundColor: '#fff',
          border: '1.5px solid #e2e8f0',
          borderRadius: '8px',
          padding: '12px',
          fontSize: '14px',
          lineHeight: '1.6',
          fontFamily: 'sans-serif',
          outline: 'none',
          color: '#334155',
          wordBreak: 'break-word',
          whiteSpace: 'pre-wrap',
        }}
      >
        {/* We start with the initial text from data */}
        {data?.text || ''}
      </div>

      <style>{`
        /* This is the magic CSS that styles the variable on the fly */
        [contenteditable] span.variable-pill {
          display: inline-flex;
          align-items: center;
          background-color: #eef2ff;
          border: 1px solid #c7d2fe;
          border-radius: 4px;
          padding: 0 6px;
          margin: 0 2px;
          color: #4338ca;
          font-weight: 600;
          font-size: 13px;
          user-select: all; /* Makes the whole pill selectable at once */
        }
      `}</style>
    </BaseNode>
  );
};
