import { useStore } from './store';
import { shallow } from 'zustand/shallow';

export const SubmitButton = () => {
    const { nodes, edges } = useStore((state) => ({
        nodes: state.nodes,
        edges: state.edges,
    }), shallow);

    const handleSubmit = async () => {
        if (nodes.length === 0) {
            alert("The pipeline is empty. Please add nodes before submitting.");
            return;
        }

        try {
            const response = await fetch('http://localhost:8000/pipelines/parse', {
                method: 'POST',
                headers: {'Content-Type': 'application/json'},
                body: JSON.stringify({ nodes, edges }),
            });

            const data = await response.json();
            alert(`Nodes: ${data.num_nodes}\nEdges: ${data.num_edges}\nIs DAG: ${data.is_dag ? 'Yes' : 'No'}`);
        } catch (error) {
            console.error("Error submitting pipeline:", error);
            alert("Failed to connect to the backend server.");
        }
    };

    return (
        <div style={{ 
            display: 'flex', 
            justifyContent: 'center', 
            alignItems: 'center',
            padding: '20px',
            borderTop: '1px solid #e2e8f0'
        }}>
            <button 
                onClick={handleSubmit} 
                style={{ 
                    padding: '12px 24px', 
                    cursor: 'pointer',
                    backgroundColor: '#6366f1', // Matches your handles and BaseNode
                    color: 'white',
                    border: 'none',
                    borderRadius: '8px',
                    fontSize: '15px',
                    fontWeight: '600',
                    transition: 'all 0.2s ease',
                    boxShadow: '0 4px 6px -1px rgba(99, 102, 241, 0.4)',
                    outline: 'none'
                }}
                onMouseEnter={(e) => {
                    e.target.style.backgroundColor = '#4f46e5';
                    e.target.style.transform = 'translateY(-2px)';
                    e.target.style.boxShadow = '0 6px 12px -2px rgba(99, 102, 241, 0.5)';
                }}
                onMouseLeave={(e) => {
                    e.target.style.backgroundColor = '#6366f1';
                    e.target.style.transform = 'translateY(0)';
                    e.target.style.boxShadow = '0 4px 6px -1px rgba(99, 102, 241, 0.4)';
                }}
            >
                Submit Pipeline
            </button>
        </div>
    );
};
