import { Handle } from 'reactflow';

export const BaseNode = ({ id, label, children, handles = [] }) => {
  return (
    <div style={{ 
      minWidth: '200px', 
      minHeight: '80px', 
      border: '2px solid #6366f1', // Improved styling (Part 2)
      borderRadius: '8px',
      background: '#fff',
      padding: '10px',
      boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)'
    }}>
      <div style={{ 
        marginBottom: '8px', 
        fontSize: '12px', 
        fontWeight: 'bold', 
        color: '#4f46e5',
        textTransform: 'uppercase' 
      }}>
        {label}
      </div>
      <div style={{ display: 'flex', flexDirection: 'column', gap: '5px' }}>
        {children}
      </div>
      {handles.map((h, index) => (
        <Handle
          key={`${id}-handle-${index}`}
          type={h.type}
          position={h.position}
          id={`${id}-${h.id}`}
          style={{ 
            background: '#6366f1', 
            width: '8px', 
            height: '8px',
            ...h.style 
          }}
        />
      ))}
    </div>
  );
};
