import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '');
  const [variables, setVariables] = useState([]);
  const textAreaRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);
  }, [currText, id, updateNodeField]);

  // These styles MUST be identical for both layers to align
  const sharedStyles = {
    padding: '12px',
    fontSize: '14px',
    lineHeight: '20px',
    fontFamily: 'monospace', // Monospace is required for perfect alignment
    whiteSpace: 'pre-wrap',
    wordWrap: 'break-word',
    width: '100%',
    minHeight: '80px',
    boxSizing: 'border-box',
    margin: 0,
    textAlign: 'left',
  };

  const renderVisualText = () => {
    // Split text by variables to insert spans
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);
    return parts.map((part, i) => {
      const match = part.match(/\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/);
      if (match) {
        return (
          <span key={i} style={{
            backgroundColor: '#eef2ff',
            border: '1px solid #c7d2fe',
            borderRadius: '4px',
            color: '#4338ca',
            fontWeight: 'bold',
            padding: '0 2px',
          }}>
            {part}
          </span>
        );
      }
      return <span key={i}>{part}</span>;
    });
  };

  return (
    <BaseNode id={id} label="Text" handles={variables.map((v, i) => ({
      type: 'target', position: Position.Left, id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))}>
      <label style={{ display: 'block', fontSize: '10px', fontWeight: 'bold', color: '#64748b', marginBottom: '8px' }}>
        CONTENT
      </label>

      <div style={{ position: 'relative', width: '220px', minHeight: '80px' }}>
        {/* LAYER 1: The Visual Highlighter (Behind) */}
        <div style={{
          ...sharedStyles,
          position: 'absolute',
          top: 0,
          left: 0,
          zIndex: 0,
          color: 'transparent', // Hide text, only show the span backgrounds
          pointerEvents: 'none',
          backgroundColor: '#f8fafc',
          borderRadius: '8px',
          border: '1px solid #e2e8f0',
        }}>
          {renderVisualText()}
        </div>

        {/* LAYER 2: The Actual Textarea (In Front) */}
        <textarea
          ref={textAreaRef}
          value={currText}
          onChange={(e) => setCurrText(e.target.value)}
          spellCheck={false}
          style={{
            ...sharedStyles,
            position: 'relative',
            zIndex: 1,
            background: 'transparent',
            color: '#334155',
            resize: 'none',
            outline: 'none',
            border: '1px solid transparent', // Keep same size as background
            overflow: 'hidden',
          }}
        />
      </div>
    </BaseNode>
  );
};
