// textNode.js

import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';

const VAR_REGEX = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '');
  const [variables, setVariables] = useState([]);

  const textAreaRef = useRef(null);
  const overlayRef = useRef(null);
  const containerRef = useRef(null);

  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    const matches = [...currText.matchAll(VAR_REGEX)].map(m => m[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);
  }, [currText, id, updateNodeField]);

  useEffect(() => {
    const ta = textareaRef.current;
    const container = containerRef.current;
    if (!ta || !container) return;

    // Reset first
    ta.style.height = "auto";
    ta.style.width = "auto";

    // Height auto grow
    ta.style.height = ta.scrollHeight + "px";

    // Width auto grow (with limits)
    const newWidth = Math.min(420, Math.max(220, ta.scrollWidth + 24));
    container.style.width = newWidth + "px";
  }, [text]);


  const renderOverlay = () => {
    const parts = currText.split(
      /(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g
    );

    return parts.map((part, i) => {
      const match = part.match(/\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/);
      if (match) {
        const name = match[1];
        return (
          <span
            key={i}
            style={{
              background: '#e0e7ff',
              color: '#3730a3',
              borderRadius: '4px',
              padding: '0 2px',
              fontWeight: 600,
            }}
          >
            {part}
          </span>
        );
      }

      return <span key={i}>{part}</span>;
    });
  };

  const textStyle = {
    fontFamily: 'monospace',
    fontSize: '14px',
    lineHeight: '22px',
    letterSpacing: '0px',
    padding: '10px',
    whiteSpace: 'pre-wrap',
    wordWrap: 'break-word',
    boxSizing: 'border-box',
  };

  return (
    <BaseNode
      id={id}
      label="Text"
      handles={variables.map((v, i) => ({
        type: 'target',
        position: Position.Left,
        id: v,
        style: {
          top: `${(i + 1) * (100 / (variables.length + 1))}%`,
        },
      }))}
    >
      <div
        ref={containerRef}
        style={{
          position: 'relative',
          minWidth: '220px',
          border: '1px solid #e5e7eb',
          borderRadius: '8px',
          background: '#fff',
        }}
      >
        <div
          ref={overlayRef}
          style={{
            ...textStyle,
            position: 'absolute',
            inset: 0,
            color: '#111827',
            pointerEvents: 'none',
            overflow: 'hidden',
          }}
        >
          {renderOverlay()}
          {currText === '' && (
            <span style={{ color: '#9ca3af' }}>
              Type ...
            </span>
          )}
        </div>

        <textarea
          ref={textAreaRef}
          value={currText}
          onChange={(e) => setCurrText(e.target.value)}
          spellCheck={false}
          onScroll={() => {
            if (overlayRef.current && textAreaRef.current) {
              overlayRef.current.scrollTop =
                textAreaRef.current.scrollTop;
            }
          }}
          style={{
            ...textStyle,
            position: 'relative',
            width: '100%',
            resize: 'none',
            border: 'none',
            outline: 'none',
            background: 'transparent',
            color: 'transparent',
            caretColor: '#111827',
            overflow: 'hidden',
          }}
        />
      </div>
    </BaseNode>
  );
};
