import { useState } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';

export const InputNode = ({ id, data }) => {
  const [currName, setCurrName] = useState(data?.inputName || id.replace('customInput-', 'input_'));
  const [inputType, setInputType] = useState(data.inputType || 'Text');

  return (
    <BaseNode id={id} label="Input" handles={[{ type: 'source', position: Position.Right, id: 'value' }]}>
      <label style={{ fontSize: '11px' }}>Name:
        <input type="text" value={currName} onChange={(e) => setCurrName(e.target.value)} style={{ width: '100%' }} />
      </label>
      <label style={{ fontSize: '11px' }}>Type:
        <select value={inputType} onChange={(e) => setInputType(e.target.value)} style={{ width: '100%' }}>
          <option value="Text">Text</option>
          <option value="File">File</option>
        </select>
      </label>
    </BaseNode>
  );
};
