import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';
import { nodeInputStyle, labelStyle, onFieldFocus, onFieldBlur } from "../styles";

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || 'type {{input}} here');
  const [variables, setVariables] = useState([]);
  const textAreaRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);

    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${Math.max(textAreaRef.current.scrollHeight, 60)}px`;
    }
  }, [currText, id, updateNodeField]);

  const handles = [
    { type: 'source', position: Position.Right, id: 'output' },
    ...variables.map((v, i) => ({
      type: 'target', position: Position.Left, id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))
  ];

  // Helper to render the visual "Pills" in the background
  const renderVisualLayer = () => {
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);
    return parts.map((part, i) => {
      const match = part.match(/\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/);
      if (match) {
        return (
          <span key={i} style={{
            display: 'inline-flex',
            alignItems: 'center',
            backgroundColor: '#eef2ff', 
            border: '1.5px solid #c7d2fe',
            borderRadius: '4px',
            padding: '0 6px',
            margin: '0 1px',
            color: '#4338ca', 
            fontSize: '12px',
            fontWeight: '600',
            verticalAlign: 'middle',
            height: '22px',
          }}>
            <span style={{ marginRight: '4px', fontSize: '10px', opacity: 0.8 }}>➡️</span>
            {match[1]}
            <span style={{ marginLeft: '6px', color: '#94a3b8', fontSize: '10px' }}>×</span>
          </span>
        );
      }
      return <span key={i}>{part}</span>;
    });
  };

  // Helper to make the text in the textarea transparent only where the pills are
  const renderTransparentMask = () => {
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);
    return parts.map((part, i) => {
      const isVar = /\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\}/.test(part);
      return (
        <span key={i} style={{ color: isVar ? 'transparent' : 'inherit' }}>
          {part}
        </span>
      );
    });
  };

  const sharedStyles = {
    ...nodeInputStyle,
    fontSize: '13px',
    lineHeight: '28px', // Increased for pill clearance
    padding: '12px',
    whiteSpace: 'pre-wrap',
    wordWrap: 'break-word',
    fontFamily: '"Fira Code", monospace', // Monospace is CRITICAL for alignment
    boxSizing: 'border-box',
    margin: 0,
    border: '1.5px solid transparent'
  };

  return (
    <BaseNode id={id} label="Text" handles={handles}>
      <label style={labelStyle}>Content</label>
      <div style={{ position: 'relative', width: '100%', cursor: 'text' }}>

        {/* LAYER 1: The Visual Pills (Background) */}
        <div style={{ 
          ...sharedStyles,
          position: 'absolute', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          zIndex: 0,
          color: '#cbd5e1', // Faint color for standard text in background
          pointerEvents: 'none',
        }}>
          {renderVisualLayer()}
        </div>

        {/* LAYER 2: The Mask (Invisible text over pills, visible elsewhere) */}
        <div style={{
          ...sharedStyles,
          position: 'absolute',
          top: 0,
          left: 0,
          width: '100%',
          height: '100%',
          zIndex: 1,
          pointerEvents: 'none',
          color: '#334155',
        }}>
          {renderTransparentMask()}
        </div>

        {/* LAYER 3: Interaction (The actual Textarea) */}
        <textarea 
          ref={textAreaRef}
          value={currText} 
          onChange={(e) => setCurrText(e.target.value)}
          onFocus={onFieldFocus}
          onBlur={onFieldBlur}
          spellCheck={false}
          style={{ 
            ...sharedStyles,
            position: 'relative',
            zIndex: 2,
            background: 'transparent',
            color: 'transparent', // The text itself is invisible, but caret is visible
            caretColor: '#6366f1',
            resize: 'none',
            overflow: 'hidden',
            border: '1.5px solid #e2e8f0',
            borderRadius: '8px'
          }} 
        />
      </div>
    </BaseNode>
  );
};
