import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '');
  const [variables, setVariables] = useState([]);
  const textAreaRef = useRef(null);
  const scrollSyncRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);

    // Auto-expand height to prevent cursor from hiding
    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = textAreaRef.current.scrollHeight + 'px';
    }
  }, [currText, id, updateNodeField]);

  // Essential styles for cursor alignment
  const sharedStyles = {
    padding: '12px',
    fontSize: '14px',
    lineHeight: '22px', // Specific line height is crucial
    fontFamily: '"Fira Code", monospace',
    whiteSpace: 'pre-wrap',
    wordWrap: 'break-word',
    width: '100%',
    minHeight: '80px',
    boxSizing: 'border-box',
    margin: 0,
    border: 'none',
    textAlign: 'left',
  };

  const renderVisualLayer = () => {
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);
    return parts.map((part, i) => {
      const isVar = /\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\}/.test(part);
      if (isVar) {
        return (
          <span key={i} style={{
            backgroundColor: '#eef2ff',
            border: '1px solid #c7d2fe',
            borderRadius: '4px',
            color: '#4338ca',
            fontWeight: '600',
            padding: '0 2px',
          }}>
            {part}
          </span>
        );
      }
      return <span key={i} style={{ color: '#334155' }}>{part}</span>;
    });
  };

  return (
    <BaseNode id={id} label="Text" handles={variables.map((v, i) => ({
      type: 'target', position: Position.Left, id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))}>
      <label style={{ display: 'block', fontSize: '10px', fontWeight: 'bold', color: '#64748b', marginBottom: '8px' }}>
        CONTENT
      </label>

      <div style={{ 
        position: 'relative', 
        width: '240px', 
        backgroundColor: '#f8fafc', 
        borderRadius: '8px', 
        border: '1px solid #e2e8f0',
        minHeight: '80px'
      }}>

        {/* VISUAL LAYER */}
        <div 
          ref={scrollSyncRef}
          style={{
            ...sharedStyles,
            position: 'relative',
            zIndex: 0,
            pointerEvents: 'none',
          }}
        >
          {renderVisualLayer()}
          {currText === '' && <span style={{ color: '#94a3b8' }}>{"Type {{variable}}..."}</span>}
        </div>

        {/* INTERACTIVE INPUT */}
        <textarea
          ref={textAreaRef}
          value={currText}
          onChange={(e) => setCurrText(e.target.value)}
          onScroll={(e) => {
            // Synchronize scroll if text gets very long
            if (scrollSyncRef.current) {
              scrollSyncRef.current.scrollTop = e.target.scrollTop;
            }
          }}
          spellCheck={false}
          style={{
            ...sharedStyles,
            position: 'absolute',
            top: 0,
            left: 0,
            zIndex: 1,
            background: 'transparent',
            color: 'transparent', // Hides the black ghost text
            caretColor: '#334155', // Makes cursor visible and responsive
            outline: 'none',
            resize: 'none',
            height: '100%',
            overflow: 'hidden',
          }}
        />
      </div>
    </BaseNode>
  );
};
