// textNode.js
import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';

const VAR_REGEX = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '');
  const [variables, setVariables] = useState([]);

  const textAreaRef = useRef(null);
  const overlayRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    const matches = [...currText.matchAll(VAR_REGEX)].map(m => m[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);
  }, [currText, id, updateNodeField]);

  useEffect(() => {
    const ta = textAreaRef.current;
    if (ta) {
      ta.style.height = 'auto';
      ta.style.height = `${ta.scrollHeight}px`;
    }
  }, [currText]);

  const renderOverlay = () => {
    // Zero-width space ensures height syncs even on trailing newlines
    const textToProcess = currText.endsWith('\n') ? currText + '\u200B' : currText;
    const parts = textToProcess.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);

    return parts.map((part, i) => {
      const match = part.match(/\{\{(\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*)\}\}/);
      if (match) {
        return (
          <span key={i} style={{ whiteSpace: 'pre-wrap' }}>
            {/* Make brackets transparent so they don't get the background color */}
            <span style={{ color: 'transparent' }}>{"{{"}</span>
            <span
              style={{
                backgroundColor: '#e0e7ff', 
                color: 'transparent', // Let the textarea text show through
                borderRadius: '4px',
                padding: '0',
                border: '1px solid #c7d2fe',
                boxSizing: 'border-box',
              }}
            >
              {match[1]}
            </span>
            <span style={{ color: 'transparent' }}>{"}}"}</span>
          </span>
        );
      }
      return <span key={i} style={{ color: 'transparent' }}>{part}</span>;
    });
  };

  const sharedStyle = {
    fontFamily: '"Fira Code", monospace',
    fontSize: '14px',
    lineHeight: '1.6',
    padding: '12px',
    margin: '0',
    whiteSpace: 'pre-wrap',
    wordBreak: 'break-all', // Critical for long strings
    boxSizing: 'border-box',
    width: '100%',
    minHeight: '40px',
  };

  return (
    <BaseNode
      id={id}
      label="Text"
      handles={variables.map((v, i) => ({
        type: 'target',
        position: Position.Left,
        id: `${id}-${v}`,
        style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` },
      }))}
    >
      <div style={{ position: 'relative', minWidth: '260px', border: '1px solid #e2e8f0', borderRadius: '8px', background: '#fff' }}>
        <div
          ref={overlayRef}
          aria-hidden="true"
          style={{
            ...sharedStyle,
            position: 'absolute',
            inset: 0,
            pointerEvents: 'none',
            zIndex: 1,
            overflow: 'hidden'
          }}
        >
          {renderOverlay()}
        </div>

        <textarea
          ref={textAreaRef}
          value={currText}
          onChange={(e) => setCurrText(e.target.value)}
          spellCheck={false}
          onScroll={() => {
            if (overlayRef.current) overlayRef.current.scrollTop = textAreaRef.current.scrollTop;
          }}
          style={{
            ...sharedStyle,
            position: 'relative',
            zIndex: 2,
            background: 'transparent',
            color: '#334155', // The real, visible text
            border: 'none',
            outline: 'none',
            resize: 'none',
            caretColor: '#6366f1',
          }}
        />
      </div>
    </BaseNode>
  );
};
