import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';
import { nodeInputStyle, labelStyle, onFieldFocus, onFieldBlur } from "../styles";

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '');
  const [variables, setVariables] = useState([]);
  const textAreaRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    // Detect variables inside {{ }} for handles
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);

    updateNodeField(id, 'text', currText);

    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${Math.max(textAreaRef.current.scrollHeight, 60)}px`;
    }
  }, [currText, id, updateNodeField]);

  const handles = [
    { type: 'source', position: Position.Right, id: 'output' },
    ...variables.map((v, i) => ({
      type: 'target', position: Position.Left, id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))
  ];

  // This function creates the visual "chips" in the background layer
  const renderHighlightedContent = () => {
    // Split text by the variable pattern but keep the brackets in the parts
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);

    return parts.map((part, i) => {
      const match = part.match(/\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/);
      if (match) {
        return (
          <span key={i} style={{ 
            display: 'inline-flex',
            alignItems: 'center',
            backgroundColor: '#eef2ff', 
            border: '1px solid #c7d2fe',
            borderRadius: '4px',
            padding: '0 5px',
            color: '#4338ca', // This color shows up "inside" the chip
            fontWeight: '600',
            fontSize: '12px',
            height: '20px',
            verticalAlign: 'middle',
            margin: '0 -1px' // Slight overlap adjustment
          }}>
            <span style={{ marginRight: '3px', fontSize: '10px' }}>➡️</span>
            {match[1]}
            <span style={{ marginLeft: '4px', color: '#94a3b8', fontSize: '10px' }}>×</span>
          </span>
        );
      }
      return <span key={i}>{part}</span>;
    });
  };

  const sharedStyles = {
    ...nodeInputStyle,
    fontSize: '13px',
    lineHeight: '26px', // High enough to fit the pill chips
    padding: '10px',
    whiteSpace: 'pre-wrap',
    wordWrap: 'break-word',
    fontFamily: 'monospace', // Essential for alignment
    margin: 0,
    boxSizing: 'border-box',
    border: '1.5px solid transparent'
  };

  return (
    <BaseNode id={id} label="Text" handles={handles}>
      <label style={labelStyle}>Content</label>
      <div style={{ position: 'relative', width: '100%', minHeight: '60px' }}>

        {/* Render Layer: Purely visual, sits behind the textarea */}
        <div style={{ 
          ...sharedStyles,
          position: 'absolute', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          zIndex: 0,
          color: 'transparent', // We hide the text here, but the <span> chips remain visible
          pointerEvents: 'none',
        }}>
          {renderHighlightedContent()}
        </div>

        {/* Interaction Layer: The user actually types here */}
        <textarea 
          ref={textAreaRef}
          value={currText} 
          onChange={(e) => setCurrText(e.target.value)}
          onFocus={onFieldFocus}
          onBlur={onFieldBlur}
          spellCheck={false}
          style={{ 
            ...sharedStyles,
            position: 'relative',
            zIndex: 1,
            background: 'transparent',
            color: '#334155',
            resize: 'none',
            overflow: 'hidden',
            caretColor: '#6366f1', // Indigo cursor
            border: '1.5px solid #e2e8f0',
            borderRadius: '6px'
          }} 
        />
      </div>
    </BaseNode>
  );
};
