import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '');
  const [variables, setVariables] = useState([]);
  const textAreaRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    // 1. Detect variables for handles
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);

    // 2. Update global store
    updateNodeField(id, 'text', currText);

    // 3. Auto-resize textarea to fit content
    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${textAreaRef.current.scrollHeight}px`;
    }
  }, [currText, id, updateNodeField]);

  // CSS Shared by both the invisible renderer and the active textarea
  const textStyles = {
    padding: '12px',
    fontSize: '14px',
    lineHeight: '22px',
    fontFamily: '"Fira Code", monospace', // Monospace is mandatory for alignment
    whiteSpace: 'pre-wrap',
    wordWrap: 'break-word',
    width: '100%',
    minHeight: '80px',
    margin: 0,
    boxSizing: 'border-box',
  };

  const renderVisualPills = () => {
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);
    return parts.map((part, i) => {
      const isVar = /\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\}/.test(part);
      if (isVar) {
        return (
          <span key={i} style={{
            backgroundColor: '#eef2ff',
            border: '1px solid #c7d2fe',
            borderRadius: '4px',
            color: '#4338ca', // Blue text for the variable
            fontWeight: '600',
            padding: '0 2px',
          }}>
            {part}
          </span>
        );
      }
      return <span key={i} style={{ color: 'transparent' }}>{part}</span>;
    });
  };

  return (
    <BaseNode id={id} label="Text" handles={variables.map((v, i) => ({
      type: 'target', position: Position.Left, id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))}>
      <label style={{ display: 'block', fontSize: '10px', fontWeight: 'bold', color: '#64748b', marginBottom: '8px' }}>
        CONTENT
      </label>

      <div style={{ position: 'relative', width: '230px' }}>
        {/* VISUAL LAYER (Behind the textarea) */}
        <div style={{
          ...textStyles,
          position: 'absolute',
          top: 0,
          left: 0,
          zIndex: 0,
          backgroundColor: '#f8fafc',
          borderRadius: '8px',
          border: '1px solid #e2e8f0',
          pointerEvents: 'none' // Clicks go through to the textarea
        }}>
          {renderVisualPills()}
        </div>

        {/* INTERACTION LAYER (The actual textarea you type in) */}
        <textarea
          ref={textAreaRef}
          value={currText}
          onChange={(e) => setCurrText(e.target.value)}
          spellCheck={false}
          placeholder="Type {{variable}}..."
          style={{
            ...textStyles,
            position: 'relative',
            zIndex: 1,
            background: 'transparent',
            color: '#334155', // Normal text is dark grey
            border: '1px solid transparent',
            outline: 'none',
            resize: 'none',
            overflow: 'hidden',
            caretColor: '#6366f1', // Bright cursor so you can see where you are
          }}
        />
      </div>
    </BaseNode>
  );
};
