import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';
import { nodeInputStyle, labelStyle } from "../styles";

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '');
  const [variables, setVariables] = useState([]);
  const textAreaRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);

    // Auto-resize logic
    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${textAreaRef.current.scrollHeight}px`;
    }
  }, [currText, id, updateNodeField]);

  const handles = [
    { type: 'source', position: Position.Right, id: 'output' },
    ...variables.map((v, i) => ({
      type: 'target', position: Position.Left, id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))
  ];

  // This renders the "Pill" visuals in a way that doesn't block typing
  const renderPills = () => {
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);
    return parts.map((part, i) => {
      const match = part.match(/\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/);
      if (match) {
        return (
          <span key={i} style={{
            display: 'inline-flex',
            alignItems: 'center',
            backgroundColor: '#eef2ff',
            border: '1px solid #c7d2fe',
            borderRadius: '4px',
            padding: '0 4px',
            color: '#4338ca',
            fontSize: '12px',
            fontWeight: '600',
            lineHeight: '18px',
            margin: '0 2px'
          }}>
            <span style={{ marginRight: '4px', fontSize: '10px' }}>➡️</span>
            {match[1]}
            <span style={{ marginLeft: '4px', color: '#94a3b8' }}>×</span>
          </span>
        );
      }
      return <span key={i}>{part}</span>;
    });
  };

  return (
    <BaseNode id={id} label="Text" handles={handles}>
      <label style={labelStyle}>CONTENT</label>

      <div style={{ 
        position: 'relative', 
        padding: '8px', 
        backgroundColor: '#fff', 
        borderRadius: '8px',
        border: '1px solid #e2e8f0',
        minHeight: '60px'
      }}>
        {/* VISUAL LAYER (Only visible when not focused or for feedback) */}
        <div style={{
          position: 'absolute',
          top: '8px',
          left: '8px',
          right: '8px',
          bottom: '8px',
          pointerEvents: 'none', // Critical: clicks go through this to textarea
          fontSize: '14px',
          fontFamily: 'monospace',
          whiteSpace: 'pre-wrap',
          wordWrap: 'break-word',
          lineHeight: '24px',
          color: 'transparent', // We only want to see the spans/pills here
          zIndex: 0
        }}>
          {renderPills()}
        </div>

        {/* INPUT LAYER (Always active) */}
        <textarea
          ref={textAreaRef}
          value={currText}
          onChange={(e) => setCurrText(e.target.value)}
          placeholder="Type {{variable}}..."
          style={{
            width: '100%',
            minHeight: '60px',
            background: 'transparent',
            border: 'none',
            outline: 'none',
            fontSize: '14px',
            fontFamily: 'monospace', // Must match visual layer
            lineHeight: '24px',
            color: '#334155',
            resize: 'none',
            position: 'relative',
            zIndex: 1,
            padding: 0,
            margin: 0,
            caretColor: '#6366f1'
          }}
        />
      </div>
    </BaseNode>
  );
};
