// textNode.js

import { useState, useEffect, useRef } from "react";
import { Position } from "reactflow";
import { BaseNode } from "./BaseNode";
import { useStore } from "../store";

// Regex
const VAR_REGEX = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
const VAR_SINGLE_REGEX = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/;

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || "");
  const [variables, setVariables] = useState([]);

  const textAreaRef = useRef(null);
  const overlayRef = useRef(null);
  const containerRef = useRef(null);

  const updateNodeField = useStore((s) => s.updateNodeField);

  // --------------------
  // Extract variables
  // --------------------
  useEffect(() => {
    const matches = [...currText.matchAll(VAR_REGEX)].map((m) => m[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, "text", currText);
  }, [currText, id, updateNodeField]);

  // --------------------
  // Auto height only (NO WIDTH RESIZE)
  // --------------------
  useEffect(() => {
    const ta = textAreaRef.current;
    const container = containerRef.current;
    if (!ta || !container) return;

    ta.style.height = "auto";
    ta.style.height = ta.scrollHeight + "px";
    container.style.height = ta.style.height;
  }, [currText]);

  // --------------------
  // Highlight renderer
  // --------------------
  const renderOverlay = () => {
    const parts = currText.split(
      /(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g
    );

    return parts.map((part, i) => {
      const match = part.match(VAR_SINGLE_REGEX);

      if (match) {
        return (
          <span
            key={i}
            style={{
              background: "#e0e7ff",
              color: "#3730a3",
              borderRadius: "4px",
              padding: "0 2px",   // ⚠️ Keep minimal padding
              fontWeight: 600,
            }}
          >
            {part}
          </span>
        );
      }

      return <span key={i}>{part}</span>;
    });
  };

  // --------------------
  // Shared text style (overlay + textarea MUST MATCH)
  // --------------------
  const textStyle = {
    fontFamily: "monospace",
    fontSize: "14px",
    lineHeight: "22px",
    padding: "10px",
    whiteSpace: "pre-wrap",
    wordBreak: "break-word",
    boxSizing: "border-box",
  };

  return (
    <BaseNode
      id={id}
      label="Text"
      handles={variables.map((v, i) => ({
        type: "target",
        position: Position.Left,
        id: v,
        style: {
          top: `${(i + 1) * (100 / (variables.length + 1))}%`,
        },
      }))}
    >
      <div
        ref={containerRef}
        style={{
          position: "relative",
          width: "260px",        // ✅ Fixed width prevents cursor jump
          border: "1px solid #e5e7eb",
          borderRadius: "8px",
          background: "#fff",
        }}
      >
        {/* Overlay */}
        <div
          ref={overlayRef}
          style={{
            ...textStyle,
            position: "absolute",
            inset: 0,
            pointerEvents: "none",
            color: "#111827",
            overflow: "hidden",
          }}
        >
          {renderOverlay()}
          {currText === "" && (
            <span style={{ color: "#9ca3af" }}>Type ...</span>
          )}
        </div>

        {/* Textarea */}
        <textarea
          ref={textAreaRef}
          value={currText}
          onChange={(e) => setCurrText(e.target.value)}
          spellCheck={false}
          style={{
            ...textStyle,
            position: "relative",
            width: "100%",
            resize: "none",
            border: "none",
            outline: "none",
            background: "transparent",
            color: "transparent",
            caretColor: "#111827",
            overflow: "hidden",
          }}
        />
      </div>
    </BaseNode>
  );
};
