import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '');
  const [variables, setVariables] = useState([]);
  const textAreaRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    // Detect variables like {{var}}
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);

    // Auto-resize textarea
    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${Math.max(textAreaRef.current.scrollHeight, 60)}px`;
    }
  }, [currText, id, updateNodeField]);

  const handles = [
    { type: 'source', position: Position.Right, id: 'output' },
    ...variables.map((v, i) => ({
      type: 'target', position: Position.Left, id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))
  ];

  // Helper to render the visual pills
  const renderPills = () => {
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);
    return parts.map((part, i) => {
      const match = part.match(/\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/);
      if (match) {
        return (
          <span key={i} style={{
            display: 'inline-flex',
            alignItems: 'center',
            backgroundColor: '#eef2ff',
            border: '1px solid #c7d2fe',
            borderRadius: '4px',
            padding: '0 5px',
            color: '#4338ca',
            fontSize: '13px',
            fontWeight: '600',
            height: '22px',
            margin: '0 1px',
            verticalAlign: 'middle'
          }}>
            <span style={{ marginRight: '4px', fontSize: '10px' }}>➡️</span>
            {match[1]}
            <span style={{ marginLeft: '5px', color: '#94a3b8', fontSize: '11px' }}>×</span>
          </span>
        );
      }
      return <span key={i} style={{ color: '#334155' }}>{part}</span>;
    });
  };

  // We define these once to ensure both the div and textarea are identical
  const textStyles = {
    fontSize: '14px',
    lineHeight: '26px',
    padding: '12px',
    fontFamily: '"Fira Code", monospace', // Monospace is CRITICAL for alignment
    whiteSpace: 'pre-wrap',
    wordWrap: 'break-word',
    margin: 0,
    boxSizing: 'border-box',
    width: '100%',
    minHeight: '60px',
  };

  return (
    <BaseNode id={id} label="Text" handles={handles}>
      <label style={{ display: 'block', color: '#64748b', fontSize: '10px', fontWeight: 'bold', marginBottom: '8px' }}>
        CONTENT
      </label>

      <div style={{ position: 'relative', width: '220px' }}>

        {/* VISUAL LAYER: Renders the pills. We set color to transparent for normal text. */}
        <div style={{ 
          ...textStyles,
          position: 'absolute', 
          top: 0, 
          left: 0, 
          zIndex: 0,
          color: 'transparent', 
          pointerEvents: 'none',
          backgroundColor: '#f8fafc',
          borderRadius: '8px',
          border: '1px solid #e2e8f0',
        }}>
          {renderPills()}
        </div>

        {/* INTERACTION LAYER: The actual typing area. */}
        <textarea 
          ref={textAreaRef}
          value={currText} 
          onChange={(e) => setCurrText(e.target.value)}
          spellCheck={false}
          placeholder="Type {{variable}}..."
          style={{ 
            ...textStyles,
            position: 'relative',
            zIndex: 1,
            background: 'transparent',
            color: '#334155', // Normal text is visible
            resize: 'none',
            overflow: 'hidden',
            border: '1px solid transparent', // Invisible border to match div
            outline: 'none',
            caretColor: '#6366f1',
          }} 
        />
      </div>
    </BaseNode>
  );
};
