// BaseNode.js

import { Handle } from 'reactflow';

export const BaseNode = ({ id, label, children, handles = [] }) => {
  return (
    <div style={{ 
      minWidth: '200px', 
      padding: '0', // Controlled padding for the header vs body
      border: '1.5px solid #e2e8f0', 
      borderRadius: '12px', 
      background: '#ffffff',
      boxShadow: '0 10px 15px -3px rgba(0, 0, 0, 0.1), 0 4px 6px -2px rgba(0, 0, 0, 0.05)',
      overflow: 'visible', // Ensures handles aren't clipped
      transition: 'border-color 0.2s ease',
    }}>
      {/* Node Header */}
      <div style={{ 
        padding: '8px 12px',
        background: '#f8fafc',
        borderBottom: '1.5px solid #e2e8f0',
        borderTopLeftRadius: '10px',
        borderTopRightRadius: '10px',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'space-between'
      }}>
        <span style={{ 
          fontSize: '12px', 
          fontWeight: '700', 
          color: '#6366f1', 
          textTransform: 'uppercase',
          letterSpacing: '0.025em'
        }}>
          {label}
        </span>
        {/* Small decorative icon or status dot */}
        <div style={{ width: '6px', height: '6px', borderRadius: '50%', background: '#cbd5e1' }}></div>
      </div>

      {/* Node Content Body */}
      <div style={{ padding: '12px', display: 'flex', flexDirection: 'column', gap: '10px' }}>
        {children}
      </div>

      {/* Modern Stylized Handles */}
      {handles.map((h, i) => (
        <Handle 
          key={i} 
          type={h.type} 
          position={h.position} 
          id={`${id}-${h.id}`} 
          style={{ 
            background: '#ffffff', 
            width: '10px', 
            height: '10px', 
            border: '2.5px solid #6366f1',
            boxShadow: '0 0 0 2px #ffffff',
            ...h.style 
          }} 
        />
      ))}
    </div>
  );
};
