import { Handle } from 'reactflow';

export const BaseNode = ({ id, label, children, handles = [] }) => {
  return (
    <div style={{ 
      minWidth: '180px', 
      padding: '12px', 
      border: '2px solid #4A90E2', 
      borderRadius: '12px', 
      background: '#ffffff',
      boxShadow: '0 8px 15px rgba(0,0,0,0.1)',
      fontFamily: 'sans-serif'
    }}>
      <div style={{ 
        fontWeight: 'bold', 
        fontSize: '14px', 
        color: '#4A90E2', 
        marginBottom: '10px', 
        borderBottom: '1px solid #f0f0f0',
        paddingBottom: '5px'
      }}>
        {label}
      </div>
      <div style={{ display: 'flex', flexDirection: 'column', gap: '10px' }}>
        {children}
      </div>
      {handles.map((h, i) => (
        <Handle 
          key={i} 
          type={h.type} 
          position={h.position} 
          id={`${id}-${h.id}`} 
          style={{ 
            background: '#4A90E2', 
            width: '10px', 
            height: '10px', 
            border: '2px solid #fff',
            ...h.style 
          }} 
        />
      ))}
    </div>
  );
};
