import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';
import { nodeInputStyle, labelStyle, onFieldFocus, onFieldBlur } from "../styles";

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '{{input}}');
  const [variables, setVariables] = useState([]);
  const textAreaRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    // Detect variables for handles
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);

    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${Math.max(textAreaRef.current.scrollHeight, 60)}px`;
    }
  }, [currText, id, updateNodeField]);

  const handles = [
    { type: 'source', position: Position.Right, id: 'output' },
    ...variables.map((v, i) => ({
      type: 'target', position: Position.Left, id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))
  ];

  const getHighlightedText = () => {
    // Regex that captures the full {{var}} but identifies the inner name
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);

    return parts.map((part, i) => {
      const match = part.match(/\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/);
      if (match) {
        // match[0] is {{variable}}, match[1] is variable
        return (
          <span key={i} style={{ 
            display: 'inline-flex',
            alignItems: 'center',
            backgroundColor: '#eef2ff', 
            border: '1px solid #c7d2fe',
            borderRadius: '4px',
            padding: '0 6px',
            margin: '0 1px',
            color: '#4338ca',
            fontSize: '12px',
            fontWeight: '600',
            verticalAlign: 'middle',
            height: '22px',
            userSelect: 'none'
          }}>
            {/* Visual Icon */}
            <span style={{ marginRight: '4px', fontSize: '10px', opacity: 0.8 }}>➡️</span>

            {/* The Text - We hide the brackets by making the pill cover the area exactly */}
            {match[1]}

            {/* Close Icon */}
            <span style={{ marginLeft: '6px', color: '#94a3b8', fontSize: '10px' }}>×</span>
          </span>
        );
      }
      return <span key={i}>{part}</span>;
    });
  };

  const sharedStyles = {
    ...nodeInputStyle,
    fontSize: '13px',
    lineHeight: '26px', // Slightly larger for the pills
    padding: '10px',
    whiteSpace: 'pre-wrap',
    wordWrap: 'break-word',
    fontFamily: 'monospace', // Monospace helps alignment significantly
  };

  return (
    <BaseNode id={id} label="Text" handles={handles}>
      <label style={labelStyle}>CONTENT</label>
      <div style={{ position: 'relative', width: '100%', cursor: 'text' }}>
        {/* Layer 0: Highlight Layer */}
        <div style={{ 
          ...sharedStyles,
          position: 'absolute', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          pointerEvents: 'none', 
          color: 'transparent', // Makes the actual text inside brackets invisible
          zIndex: 0,
          border: '1.5px solid transparent',
          boxSizing: 'border-box'
        }}>
          {getHighlightedText()}
        </div>

        {/* Layer 1: Editable Textarea */}
        <textarea 
          ref={textAreaRef}
          value={currText} 
          onChange={(e) => setCurrText(e.target.value)}
          onFocus={onFieldFocus}
          onBlur={onFieldBlur}
          spellCheck={false}
          style={{ 
            ...sharedStyles,
            background: 'transparent',
            position: 'relative',
            zIndex: 1,
            resize: 'none',
            overflow: 'hidden',
            color: '#334155',
            caretColor: '#6366f1',
            boxSizing: 'border-box',
            border: '1.5px solid #e2e8f0'
          }} 
        />
      </div>
    </BaseNode>
  );
};
