import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';
import { nodeInputStyle, labelStyle, onFieldFocus, onFieldBlur } from "../styles";

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '{{input}}');
  const [variables, setVariables] = useState([]);
  const textAreaRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);

    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${Math.max(textAreaRef.current.scrollHeight, 60)}px`;
    }
  }, [currText, id, updateNodeField]);

  const handles = [
    { type: 'source', position: Position.Right, id: 'output' },
    ...variables.map((v, i) => ({
      type: 'target', position: Position.Left, id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))
  ];

  // Logic to render variables as Pill/Chips similar to VectorShift
  const getHighlightedText = () => {
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);
    return parts.map((part, i) => {
      const match = part.match(/\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/);
      if (match) {
        return (
          <span key={i} style={{ 
            display: 'inline-flex',
            alignItems: 'center',
            backgroundColor: '#eef2ff', // Soft indigo background
            border: '1px solid #c7d2fe', // Light indigo border
            borderRadius: '4px',
            padding: '0 4px',
            margin: '0 2px',
            color: '#4338ca', // Dark indigo text
            fontSize: '11px',
            fontWeight: '600',
            verticalAlign: 'middle',
            lineHeight: '1.4'
          }}>
            {/* Small icon similar to the reference image */}
            <span style={{ marginRight: '4px', fontSize: '10px' }}>➡️</span>
            {match[1]}
            <span style={{ marginLeft: '4px', color: '#94a3b8', cursor: 'default' }}>×</span>
          </span>
        );
      }
      return part;
    });
  };

  return (
    <BaseNode id={id} label="Text" handles={handles}>
      <label style={labelStyle}>CONTENT</label>
      <div style={{ position: 'relative', width: '100%' }}>
        {/* Render Layer: Visualizing the Pills */}
        <div style={{ 
          ...nodeInputStyle, 
          position: 'absolute', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          pointerEvents: 'none', 
          color: 'transparent', 
          whiteSpace: 'pre-wrap', 
          wordWrap: 'break-word',
          zIndex: 0,
          lineHeight: '1.8' // Added spacing for pills
        }}>
          {getHighlightedText()}
        </div>

        {/* Interaction Layer: The transparent Textarea */}
        <textarea 
          ref={textAreaRef}
          value={currText} 
          onChange={(e) => setCurrText(e.target.value)}
          onFocus={onFieldFocus}
          onBlur={onFieldBlur}
          style={{ 
            ...nodeInputStyle, 
            background: 'transparent',
            position: 'relative',
            zIndex: 1,
            resize: 'none',
            overflow: 'hidden',
            color: '#334155',
            caretColor: '#6366f1',
            lineHeight: '1.8' // Must match the render layer exactly
          }} 
        />
      </div>
    </BaseNode>
  );
};
