import { useStore } from './store';
import { shallow } from 'zustand/shallow';
import Swal from 'sweetalert2';
import withReactContent from 'sweetalert2-react-content';

const MySwal = withReactContent(Swal);

export const SubmitButton = () => {
  const { nodes, edges } = useStore(
    (state) => ({
      nodes: state.nodes,
      edges: state.edges,
    }),
    shallow
  );

  const handleSubmit = async () => {
    if (nodes.length === 0) {
      MySwal.fire({
        icon: 'warning',
        title: 'Pipeline Empty',
        text: 'Please add nodes before submitting.',
        confirmButtonColor: '#6366f1',
      });
      return;
    }

    try {
      const response = await fetch('http://localhost:8000/pipelines/parse', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({ nodes, edges }),
      });

      const data = await response.json();

      MySwal.fire({
        icon: data.is_dag ? 'success' : 'info',
        title: 'Pipeline Analysis',
        html: `
          <b>Nodes:</b> ${data.num_nodes}<br/>
          <b>Edges:</b> ${data.num_edges}<br/>
          <b>Is DAG:</b> ${data.is_dag ? 'Yes ✅' : 'No ❌'}
        `,
        confirmButtonColor: '#6366f1',
      });

    } catch (error) {
      console.error("Error submitting pipeline:", error);
      MySwal.fire({
        icon: 'error',
        title: 'Server Error',
        text: 'Failed to connect to backend server.',
        confirmButtonColor: '#ef4444',
      });
    }
  };

  return (
    <div style={{ 
      display: 'flex', 
      justifyContent: 'center', 
      alignItems: 'center',
      padding: '20px',
      borderTop: '1px solid #e2e8f0'
    }}>
      <button 
        onClick={handleSubmit} 
        style={{ 
          padding: '12px 24px', 
          cursor: 'pointer',
          backgroundColor: '#6366f1', 
          color: 'white',
          border: 'none',
          borderRadius: '8px',
          fontSize: '15px',
          fontWeight: '600',
          transition: 'all 0.2s ease',
          boxShadow: '0 4px 6px -1px rgba(99, 102, 241, 0.4)',
          outline: 'none'
        }}
      >
        Submit Pipeline
      </button>
    </div>
  );
};
