// textNode.js

import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';

const VAR_REGEX = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '');
  const [variables, setVariables] = useState([]);

  const textAreaRef = useRef(null);
  const overlayRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    const matches = [...currText.matchAll(VAR_REGEX)].map(m => m[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);
  }, [currText, id, updateNodeField]);

  // Pixel-perfect height synchronization
  useEffect(() => {
    const ta = textAreaRef.current;
    if (ta) {
      ta.style.height = 'auto';
      ta.style.height = `${ta.scrollHeight}px`;
    }
  }, [currText]);

  const renderOverlay = () => {
    // Zero-width space ensures trailing newlines are rendered for height sync
    const textToProcess = currText.endsWith('\n') ? currText + '\u200B' : currText;
    const parts = textToProcess.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);

    return parts.map((part, i) => {
      if (part.match(VAR_REGEX)) {
        return (
          <span
            key={i}
            style={{
              backgroundColor: '#e0e7ff',
              color: 'transparent', // Crucial: Hide overlay text to prevent misalignment glitches
              borderRadius: '4px',
              padding: '0',
              border: '1px solid #c7d2fe',
              boxSizing: 'border-box'
            }}
          >
            {part}
          </span>
        );
      }
      return <span key={i}>{part}</span>;
    });
  };

  // Identical styles for both layers to ensure zero drift
  const sharedStyle = {
    fontFamily: '"Fira Code", "Courier New", monospace', // Monospace is mandatory for alignment
    fontSize: '14px',
    lineHeight: '1.5',
    padding: '12px',
    margin: '0',
    whiteSpace: 'pre-wrap',
    wordBreak: 'break-all', // Matches behavior across layers for long strings
    boxSizing: 'border-box',
    width: '100%',
    minHeight: '40px',
  };

  return (
    <BaseNode
      id={id}
      label="Text"
      handles={variables.map((v, i) => ({
        type: 'target',
        position: Position.Left,
        id: `${id}-${v}`, // Unique ID per node variable
        style: {
          top: `${(i + 1) * (100 / (variables.length + 1))}%`,
        },
      }))}
    >
      <div
        style={{
          position: 'relative',
          minWidth: '260px',
          border: '1px solid #e2e8f0',
          borderRadius: '8px',
          background: '#fff',
          overflow: 'hidden'
        }}
      >
        {/* THE OVERLAY (Bottom Layer: Just the highlights) */}
        <div
          ref={overlayRef}
          aria-hidden="true"
          style={{
            ...sharedStyle,
            position: 'absolute',
            inset: 0,
            color: 'transparent', 
            pointerEvents: 'none',
            zIndex: 1,
            overflow: 'hidden'
          }}
        >
          {renderOverlay()}
        </div>

        {/* THE TEXTAREA (Top Layer: The actual visible text) */}
        <textarea
          ref={textAreaRef}
          value={currText}
          onChange={(e) => setCurrText(e.target.value)}
          spellCheck={false}
          onScroll={() => {
            if (overlayRef.current) overlayRef.current.scrollTop = textAreaRef.current.scrollTop;
          }}
          style={{
            ...sharedStyle,
            position: 'relative',
            zIndex: 2,
            background: 'transparent',
            color: '#334155', // Real text is visible here
            border: 'none',
            outline: 'none',
            resize: 'none',
            caretColor: '#6366f1',
            overflow: 'hidden'
          }}
        />
      </div>
    </BaseNode>
  );
};
