import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '');
  const [variables, setVariables] = useState([]);

  const textAreaRef = useRef(null);
  const containerRef = useRef(null);

  const updateNodeField = useStore((s) => s.updateNodeField);

  /**
   * Detect {{variable}} and create handles
   */
  useEffect(() => {
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(m => m[1]);
    const uniqueVars = [...new Set(matches)];

    setVariables(uniqueVars);
    updateNodeField(id, 'text', currText);
  }, [currText, id, updateNodeField]);

  /**
   * Auto resize node width and height
   */
  useEffect(() => {
    if (!textAreaRef.current || !containerRef.current) return;

    // Auto height
    textAreaRef.current.style.height = 'auto';
    textAreaRef.current.style.height =
      textAreaRef.current.scrollHeight + 'px';

    // Auto width (simple estimation)
    const length = Math.max(currText.length, 20);
    const width = Math.min(450, 180 + length * 4);
    containerRef.current.style.width = `${width}px`;
  }, [currText]);

  /**
   * Highlight variables inside {{ }}
   */
  const renderHighlightedText = () => {
    const parts = currText.split(
      /(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g
    );

    return parts.map((part, index) => {
      const isVariable = /^\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\}$/.test(part);

      if (isVariable) {
        const name = part.replace(/[{}]/g, '').trim();
        return (
          <span
            key={index}
            style={{
              background: '#e0e7ff',
              color: '#3730a3',
              padding: '2px 6px',
              borderRadius: '4px',
              fontWeight: 600,
            }}
          >
            {name}
          </span>
        );
      }

      return <span key={index}>{part}</span>;
    });
  };

  const textStyle = {
    padding: '10px',
    fontSize: '14px',
    lineHeight: '22px',
    fontFamily: 'monospace',
    whiteSpace: 'pre-wrap',
    wordWrap: 'break-word',
  };

  return (
    <BaseNode
      id={id}
      label="Text"
      handles={variables.map((v, i) => ({
        type: 'target',
        position: Position.Left,
        id: `${id}-${v}`,
        style: {
          top: `${(i + 1) * (100 / (variables.length + 1))}%`,
        },
      }))}
    >
      <div
        ref={containerRef}
        style={{
          position: 'relative',
          minWidth: '180px',
          border: '1px solid #e5e7eb',
          borderRadius: '8px',
          background: '#f9fafb',
        }}
      >
        {/* Highlight layer */}
        <div
          style={{
            ...textStyle,
            pointerEvents: 'none',
            color: '#111827',
          }}
        >
          {renderHighlightedText()}
          {currText === '' && (
            <span style={{ color: '#9ca3af' }}>
              Type {'{{variable}}'}...
            </span>
          )}
        </div>

        {/* Transparent input */}
        <textarea
          ref={textAreaRef}
          value={currText}
          onChange={(e) => setCurrText(e.target.value)}
          spellCheck={false}
          style={{
            ...textStyle,
            position: 'absolute',
            inset: 0,
            background: 'transparent',
            color: 'transparent',
            caretColor: '#111827',
            border: 'none',
            resize: 'none',
            outline: 'none',
            overflow: 'hidden',
          }}
        />
      </div>
    </BaseNode>
  );
};
