import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';
import { nodeInputStyle, labelStyle, onFieldFocus, onFieldBlur } from "../styles";

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '{{input}}');
  const [variables, setVariables] = useState([]);
  const textAreaRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);

  useEffect(() => {
    const regex = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;
    const matches = [...currText.matchAll(regex)].map(match => match[1]);
    setVariables([...new Set(matches)]);

    // Sync with store
    updateNodeField(id, 'text', currText);

    // Auto-resize logic
    if (textAreaRef.current) {
      textAreaRef.current.style.height = 'auto';
      textAreaRef.current.style.height = `${Math.max(textAreaRef.current.scrollHeight, 40)}px`;
    }
  }, [currText, id, updateNodeField]);

  const handles = [
    { type: 'source', position: Position.Right, id: 'output' },
    ...variables.map((v, i) => ({
      type: 'target', 
      position: Position.Left, 
      id: v,
      style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` }
    }))
  ];

  const getHighlightedText = () => {
    const parts = currText.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);
    return parts.map((part, i) => {
      const isVar = /\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\}/.test(part);
      return isVar ? (
        <span key={i} style={{ 
            color: '#6366f1', 
            fontWeight: '700', 
            backgroundColor: 'rgba(99, 102, 241, 0.1)', 
            borderRadius: '4px',
            padding: '0 2px'
        }}>
          {part}
        </span>
      ) : part;
    });
  };

  return (
    <BaseNode id={id} label="Text" handles={handles}>
      <label style={labelStyle}>CONTENT</label>
      <div style={{ position: 'relative', width: '100%', minHeight: '40px' }}>
        {/* Layer 1: The Highlighting Layer (Shows variables) */}
        <div style={{ 
          ...nodeInputStyle, 
          position: 'absolute', 
          top: 0, 
          left: 0, 
          width: '100%', 
          height: '100%', 
          pointerEvents: 'none', 
          color: 'transparent', // Hide plain text, show only spans
          whiteSpace: 'pre-wrap', 
          wordWrap: 'break-word',
          zIndex: 0,
          border: '1.5px solid transparent' // Prevent offset
        }}>
          {getHighlightedText()}
        </div>

        {/* Layer 2: The Actual Input (Text is visible here) */}
        <textarea 
          ref={textAreaRef}
          value={currText} 
          onChange={(e) => setCurrText(e.target.value)}
          onFocus={onFieldFocus}
          onBlur={onFieldBlur}
          style={{ 
            ...nodeInputStyle, 
            background: 'transparent', // Let layer 1 show through
            position: 'relative',
            zIndex: 1,
            resize: 'none',
            overflow: 'hidden',
            display: 'block',
            color: '#334155', // Actual text color
            caretColor: '#6366f1'
          }} 
        />
      </div>
    </BaseNode>
  );
};
