import React, { useState } from "react";
import { useStore } from "../store";

export default function Submit() {
  const nodes = useStore((state) => state.nodes);
  const edges = useStore((state) => state.edges);

  const [result, setResult] = useState(null);

  const handleSubmit = () => {
    const payload = {
      nodes,
      edges,
    };

    console.log("Submitted Data:", payload);
    setResult(payload);
  };

  return (
    <div style={styles.page}>
      <div style={styles.glassCard}>
        <h2 style={styles.title}>ðŸš€ Flow Submit Panel</h2>

        <div style={styles.infoRow}>
          <div style={styles.statBox}>
            <span style={styles.label}>Nodes</span>
            <span style={styles.value}>{nodes.length}</span>
          </div>

          <div style={styles.statBox}>
            <span style={styles.label}>Edges</span>
            <span style={styles.value}>{edges.length}</span>
          </div>
        </div>

        <button style={styles.button} onClick={handleSubmit}>
          Submit Flow
        </button>

        {result && (
          <pre style={styles.resultBox}>
            {JSON.stringify(result, null, 2)}
          </pre>
        )}
      </div>
    </div>
  );
}

/* ------------------ Glassmorphism Styles ------------------ */

const styles = {
  page: {
    height: "100vh",
    background:
      "linear-gradient(135deg, #667eea 0%, #764ba2 100%)",
    display: "flex",
    alignItems: "center",
    justifyContent: "center",
  },

  glassCard: {
    width: "420px",
    padding: "24px",
    borderRadius: "18px",
    background: "rgba(255, 255, 255, 0.15)",
    backdropFilter: "blur(14px)",
    WebkitBackdropFilter: "blur(14px)",
    boxShadow: "0 8px 32px rgba(0,0,0,0.25)",
    border: "1px solid rgba(255,255,255,0.25)",
    color: "#fff",
  },

  title: {
    textAlign: "center",
    marginBottom: "20px",
    fontWeight: 600,
    letterSpacing: "0.5px",
  },

  infoRow: {
    display: "flex",
    justifyContent: "space-between",
    gap: "16px",
    marginBottom: "20px",
  },

  statBox: {
    flex: 1,
    padding: "12px",
    borderRadius: "12px",
    background: "rgba(255,255,255,0.18)",
    textAlign: "center",
  },

  label: {
    fontSize: "13px",
    opacity: 0.85,
  },

  value: {
    display: "block",
    fontSize: "22px",
    fontWeight: 700,
    marginTop: "4px",
  },

  button: {
    width: "100%",
    padding: "12px",
    borderRadius: "12px",
    border: "none",
    cursor: "pointer",
    fontSize: "16px",
    fontWeight: 600,
    background: "rgba(255,255,255,0.85)",
    color: "#333",
    transition: "0.2s ease-in-out",
  },

  resultBox: {
    marginTop: "16px",
    maxHeight: "240px",
    overflow: "auto",
    padding: "12px",
    borderRadius: "10px",
    fontSize: "12px",
    background: "rgba(0,0,0,0.35)",
    color: "#00ffcc",
  },
};
