// TextNode.js
import { useState, useEffect, useRef } from 'react';
import { Position } from 'reactflow';
import { BaseNode } from './BaseNode';
import { useStore } from '../store';

const VAR_REGEX = /\{\{\s*([a-zA-Z_$][a-zA-Z0-9_$]*)\s*\}\}/g;

export const TextNode = ({ id, data }) => {
  const [currText, setCurrText] = useState(data?.text || '');
  const [dimensions, setDimensions] = useState({ width: 260, height: 40 });
  const [variables, setVariables] = useState([]);

  const textAreaRef = useRef(null);
  const overlayRef = useRef(null);
  const containerRef = useRef(null);
  const updateNodeField = useStore((s) => s.updateNodeField);
  const updateNodeDimensions = useStore((s) => s.updateNodeDimensions); // Add this to your store

  useEffect(() => {
    const matches = [...currText.matchAll(VAR_REGEX)].map(m => m[1]);
    setVariables([...new Set(matches)]);
    updateNodeField(id, 'text', currText);
  }, [currText, id, updateNodeField]);

  useEffect(() => {
    const ta = textAreaRef.current;
    if (ta) {
      // Auto height
      ta.style.height = 'auto';
      const newHeight = Math.max(40, ta.scrollHeight);
      ta.style.height = `${newHeight}px`;

      // Auto width  
      const newWidth = Math.max(260, ta.scrollWidth + 24);

      // Update local dimensions
      setDimensions({ width: newWidth, height: newHeight });

      // Update store for React Flow
      if (updateNodeDimensions) {
        updateNodeDimensions(id, { width: newWidth, height: newHeight });
      }

      // Force container resize
      if (containerRef.current) {
        containerRef.current.style.width = `${newWidth}px`;
        containerRef.current.style.height = `${newHeight}px`;
      }
    }
  }, [currText, id, updateNodeDimensions]);

  const renderOverlay = () => {
    const textToProcess = currText.endsWith('\n') ? currText + '\u200B' : currText;
    const parts = textToProcess.split(/(\{\{\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*\}\})/g);

    return parts.map((part, i) => {
      const match = part.match(/\{\{(\s*[a-zA-Z_$][a-zA-Z0-9_$]*\s*)\}\}/);
      if (match) {
        return (
          <span key={i} style={{ whiteSpace: 'pre-wrap' }}>
            <span style={{ color: 'transparent' }}>{"{{"}</span>
            <span
              style={{
                backgroundColor: '#e0e7ff', 
                color: 'transparent',
                borderRadius: '4px',
                padding: '0 2px',
                border: '1px solid #c7d2fe',
                boxSizing: 'border-box',
              }}
            >
              {match[1]}
            </span>
            <span style={{ color: 'transparent' }}>{"}}"}</span>
          </span>
        );
      }
      return <span key={i} style={{ color: 'transparent' }}>{part}</span>;
    });
  };

  const sharedStyle = {
    fontFamily: '"Fira Code", monospace',
    fontSize: '14px',
    lineHeight: '1.6',
    padding: '12px',
    margin: '0',
    whiteSpace: 'pre-wrap',
    wordBreak: 'break-word',
    boxSizing: 'border-box',
    border: 'none',
    outline: 'none',
    resize: 'none',
  };

  return (
    <BaseNode
      id={id}
      label="Text"
      handles={variables.map((v, i) => ({
        type: 'target',
        position: Position.Left,
        id: `${id}-${v}`,
        style: { top: `${(i + 1) * (100 / (variables.length + 1))}%` },
      }))}
    >
      <div 
        ref={containerRef}
        style={{ 
          position: 'relative', 
          width: dimensions.width, 
          height: dimensions.height,
          minWidth: '260px',
          minHeight: '40px',
          border: '1px solid #e2e8f0', 
          borderRadius: '8px', 
          background: '#fff',
          overflow: 'hidden'
        }}
      >
        <div
          ref={overlayRef}
          aria-hidden="true"
          style={{
            ...sharedStyle,
            position: 'absolute',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            pointerEvents: 'none',
            zIndex: 1,
            overflow: 'hidden',
            background: 'transparent'
          }}
        >
          {renderOverlay()}
        </div>

        <textarea
          ref={textAreaRef}
          value={currText}
          onChange={(e) => setCurrText(e.target.value)}
          spellCheck={false}
          onScroll={(e) => {
            if (overlayRef.current) {
              overlayRef.current.scrollTop = e.target.scrollTop;
            }
          }}
          style={{
            ...sharedStyle,
            position: 'relative',
            zIndex: 2,
            background: 'transparent',
            color: '#334155',
            width: '100%',
            height: '100%',
            caretColor: '#6366f1',
          }}
        />
      </div>
    </BaseNode>
  );
};
